import React, {useState} from 'react';
import { View, Text, StyleSheet, Image, TouchableOpacity, TextInput, Alert } from 'react-native';
import Icon from 'react-native-vector-icons/MaterialIcons';
import {db} from './config'
import * as firebase from 'firebase/app'
import 'firebase/auth'


const addItem = (props, email, username, pass) => {

  const EmailValidator = require('email-deep-validator');

  const emailValidator = new EmailValidator();

  const { wellFormed, validDomain, validMailbox } = emailValidator.verify(email);

  if (! validDomain){
    alert('Domain does not Exist');
  }

  else {
    alert('Domain does Exist');
  }

// also add in something to make sure their account does not exist, time permitting

  if (email != '' && username != ''&& pass != '') {

    db.ref('/Users').push({

      Email: email,
      UserName: username,
      Password: pass
    });

    props.navigate('Search Page', { paramKey: username,})
  }

  else{
    if (email == '') {
      alert ('Please enter Valid Email')
      // alert for the email
    }

    else if (username == '') {
      // alert for username
      alert ('Please enter Valid Username')
    }

    else {
      // alert for the password being empty
      alert ('Please enter Valid Password')
    }
  }

}

const Register = ({navigation}) => {

  const [email, setemail] = useState('');

  const [username, setusername] = useState('');

  const [pass1, setpass1] = useState('');

  const [pass2, setpass2] = useState('');

  return (

    <View style={styles.container}>

      <View style = {styles.circle}> 
        <Image source={require('./images/logo.png')} style={styles.logo} />
      </View>

      <View style = {styles.head}> 
          <Text style ={styles.text}>Sign Up</Text>
      </View>

      <View style = {styles.inputContainer}> 
          <TextInput
          style = {styles.username}
          placeholder = 'Email'
          onChangeText = {text => setemail(text)}
          placeholderTextColor = 'gray'/> 
      </View>

      <View style = {styles.input}> 
          <TextInput
          style = {styles.username}
          onChangeText = {text => setusername(text)}
          placeholder = 'Username'
          placeholderTextColor = 'gray'/> 
      </View>

      <View style = {styles.input}> 
          <TextInput
          style = {styles.password}
          placeholder = 'Password'
          onChangeText = {text => setpass1(text)}
          placeholderTextColor = 'gray'
          secureTextEntry = {true}/> 
      </View>

      <View style = {styles.input}> 
          <TextInput
          style = {styles.password}
          placeholder = 'Confirm Password'
          // over here add a function to make sure that the passwords are the same
          onChangeText = {text => setpass2(text)}
          placeholderTextColor = 'gray'
          secureTextEntry = {true}/> 
      </View>

      <View>

        <TouchableOpacity 
            onPress={ () => addItem(navigation, email, username, pass1)} 
            style={styles.signUpBtn}>
            <Text style={styles.signIntext}>Sign Up</Text>
        </TouchableOpacity>

      </View>

      <View style ={styles.logInView}>

        <Text style={styles.logInText}>
          Already have an account?
        </Text>

        <TouchableOpacity 
          onPress={() => navigation.navigate('Home Page')} 
          >
          <Text style = {styles.logIn}> Login here </Text>
        </TouchableOpacity>


      </View>

    </View>
  );
};

