const Register = ({navigation}) => {

  const [email, setemail] = useState('');

  const [username, setusername] = useState('');

  const [pass1, setpass1] = useState('');

  const [pass2, setpass2] = useState('');

  const __doSignUp = () => {
    if (!email) {
      setError("Email required *")
      setValid(false)
      return
    } else if (!pass1 && pass1==pass2 && pass1.trim() && pass1.length > 6) {
      setError("Weak password, minimum 5 chars")
      setValid(false)
      return
    } else if (!__isValidEmail(email)) {
      setError("Invalid Email")
      setValid(false)
      return
    }
  
    __doCreateUser(email, pass1)
  }
  
  const __doCreateUser = async (email, pass1) => {
    try {
      let response = await auth().createUserWithEmailAndPassword(email, pass1)
      if (response) {
        console.log(tag, "?", response)
      }
    } catch (e) {
      console.error(e.message)
    }
  }

  return (

    <View style={styles.container}>

      <View style = {styles.circle}> 
        <Image source={require('./images/logo.png')} style={styles.logo} />
      </View>

      <View style = {styles.head}> 
          <Text style ={styles.text}>Sign Up</Text>
      </View>

      <View style = {styles.inputContainer}> 
          <TextInput
          style = {styles.username}
          placeholder = 'Email'
          onChangeText = {text => setemail(text)}
          value={this.state.email}
          placeholderTextColor = 'gray'/> 
      </View>

      <View style = {styles.input}> 
          <TextInput
          style = {styles.username}
          onChangeText = {text => setusername(text)}
          value={this.state.username}
          placeholder = 'Username'
          placeholderTextColor = 'gray'/> 
      </View>

      <View style = {styles.input}> 
          <TextInput
          style = {styles.password}
          placeholder = 'Password'
          onChangeText = {text => setpass1(text)}
          value={this.state.setpass1}
          placeholderTextColor = 'gray'
          secureTextEntry = {true}/> 
      </View>

      <View style = {styles.input}> 
          <TextInput
          style = {styles.password}
          placeholder = 'Confirm Password'
          // over here add a function to make sure that the passwords are the same
          onChangeText = {text => setpass2(text)}
          value={this.state.setpass2}
          placeholderTextColor = 'gray'
          secureTextEntry = {true}/> 
      </View>

      <View>

        <TouchableOpacity 
            onPress={ () => __doSignUp(navigation, email, username, pass1, pass2)} 
            style={styles.signUpBtn}>
            <Text style={styles.signIntext}>Sign Up</Text>
        </TouchableOpacity>

      </View>

      <View style ={styles.logInView}>

        <Text style={styles.logInText}>
          Already have an account?
        </Text>

        <TouchableOpacity 
          onPress={() => navigation.navigate('Home Page')} 
          >
          <Text style = {styles.logIn}> Login here </Text>
        </TouchableOpacity>


      </View>

    </View>
  );
};

const styles = StyleSheet.create({

    circle: {
      width: 180,
      height: 180,
      borderRadius: 180 / 2,
      backgroundColor: "#0B0C10",
      borderColor: '#66FCF1',
      borderWidth: 1,
      alignSelf: 'center',
      marginTop: 100,
      alignContent: "center",
      //marginBottom: 50
