      borderWidth: 1
      //borderWidth: 1,   
    },
    
    signIntext:{
      justifyContent: 'center',
      textAlign: 'center',
      fontFamily: "Verdana-Bold",
      fontSize: 20,
      color: '#fff5ee'
      //fontWeight: 'bold'
    },

    logInText:{

        fontFamily: "Verdana",
        fontSize: 15,
        color: 'gray',
      },
  
    logInView: {
      
      flexDirection: 'row', 
      marginTop: 50, 
      alignSelf :'center'
    },

    logIn: {

      fontFamily: "Verdana",
      fontSize: 15,
      color: '#66FCF1',
    },

    container: {
      flex: 1,
      alignContent: 'center',
      flexDirection: "column",
      backgroundColor: '#0B0C10',
    },
  
    head: {
        marginLeft: 0,
        flexDirection: "column",
        alignSelf: 'center',
        marginTop: 50
      },

    horizontal: {
      marginTop: 300,
      alignSelf: 'center',
      flexDirection: "row"
    },

    image: {
        flex: 1,
        resizeMode: "cover",
        justifyContent: "center"
    },

    text:{
        textAlign: 'left',
        fontFamily: "Verdana-Bold",
        fontSize: 40,
        color: "#66FCF1",
        alignSelf: 'flex-start'
    },

    signintext:{
      alignSelf: "center",
      fontFamily: "Verdana-Bold",
      fontSize: 20,
      color: '#000000'
    },
 

    inputContainer: {
      flex: 3,
      maxHeight: 45,
      width: 350,
