import {db} from './config'

    var dataArray = [];

    db.ref('/Computers').once('value', function(snapshot) {

        var temparr = [];

        snapshot.forEach(function(snap) {
            var item;
            item.val = snap.val()
            item.key = snap.key()
            temparr.push(item)

          });

          dataArray = temparr

    });

export {dataArray};
